import React, { useContext } from "react";
import axios from "axios";
import { CartContext } from "../context/CartContext";
import {
  Container,
  Grid,
  Paper,
  Typography,
  Box,
  IconButton,
  Button,
  TextField,
  Divider,
  Chip,
} from "@mui/material";
import { Add, Remove, Delete as DeleteIcon, Payment, ArrowBack } from "@mui/icons-material";
import { Link as RouterLink } from "react-router-dom";

const Cart = () => {
  const { cart, removeFromCart, updateQuantity, totalAmount, clearCart } = useContext(CartContext);

  const placeOrder = async () => {
    const userId = localStorage.getItem("userId");
    if (!userId) return alert("Please login first!");
    const order = { userId, items: cart, totalAmount };
    await axios.post("http://localhost:5000/api/orders", order);
    alert("Order placed successfully!");
    clearCart();
  };

  if (cart.length === 0) {
    return (
      <Container maxWidth="md" sx={{ py: 8, textAlign: "center" }}>
        <Typography variant="h5" gutterBottom>ðŸ›’ Your cart is empty</Typography>
        <Typography color="text.secondary" sx={{ mb: 3 }}>Add some tasty dishes to get started.</Typography>
        <Button component={RouterLink} to="/" startIcon={<ArrowBack />} variant="contained">
          Continue Shopping
        </Button>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Grid container spacing={3}>
        <Grid item xs={12} md={8}>
          <Paper elevation={1} sx={{ p: 2, borderRadius: 2 }}>
            {cart.map((item) => (
              <Box key={item._id} sx={{ display: "flex", alignItems: "center", py: 2 }}>
                <Box sx={{ flexGrow: 1 }}>
                  <Typography variant="subtitle1" fontWeight={700}>{item.name}</Typography>
                  <Chip size="small" label={item.category} variant="outlined" sx={{ mt: 0.5 }} />
                </Box>

                <Box sx={{ display: "flex", alignItems: "center", mr: 2 }}>
                  <IconButton size="small" onClick={() => updateQuantity(item._id, item.quantity - 1)}>
                    <Remove fontSize="small" />
                  </IconButton>
                  <TextField
                    type="number"
                    size="small"
                    value={item.quantity}
                    onChange={(e) => updateQuantity(item._id, Math.max(1, Number(e.target.value)))}
                    inputProps={{ min: 1, style: { width: 48, textAlign: "center" } }}
                    sx={{ mx: 1 }}
                  />
                  <IconButton size="small" onClick={() => updateQuantity(item._id, item.quantity + 1)}>
                    <Add fontSize="small" />
                  </IconButton>
                </Box>

                <Typography sx={{ width: 96, textAlign: "right", mr: 2 }}>
                  â‚¹{(Number(item.price) * Number(item.quantity)).toFixed(2)}
                </Typography>

                <IconButton color="error" onClick={() => removeFromCart(item._id)}>
                  <DeleteIcon />
                </IconButton>
              </Box>
            ))}
            <Divider sx={{ my: 2 }} />
            <Box sx={{ display: "flex", justifyContent: "space-between" }}>
              <Button variant="outlined" color="error" startIcon={<DeleteIcon />} onClick={clearCart}>
                Clear Cart
              </Button>
              <Typography variant="h6" fontWeight={800}>Total: â‚¹{Number(totalAmount).toFixed(2)}</Typography>
            </Box>
          </Paper>
        </Grid>

        <Grid item xs={12} md={4}>
          <Paper elevation={1} sx={{ p: 2, borderRadius: 2, position: { md: "sticky" }, top: 16 }}>
            <Typography variant="h6" fontWeight={800} gutterBottom>Order Summary</Typography>
            <Box sx={{ display: "flex", justifyContent: "space-between", mb: 1 }}>
              <Typography color="text.secondary">Subtotal</Typography>
              <Typography>â‚¹{Number(totalAmount).toFixed(2)}</Typography>
            </Box>
            <Box sx={{ display: "flex", justifyContent: "space-between", mb: 1 }}>
              <Typography color="text.secondary">Delivery</Typography>
              <Typography>Free</Typography>
            </Box>
            <Divider sx={{ my: 2 }} />
            <Button fullWidth variant="contained" startIcon={<Payment />} onClick={placeOrder} sx={{ py: 1.2, borderRadius: 2, fontWeight: 700 }}>
              Place Order
            </Button>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Cart;
