import React, { useContext } from "react";
import { Link as RouterLink } from "react-router-dom";
import { AppBar, Toolbar, Typography, Badge, IconButton, Box, Button, Container } from "@mui/material";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import RestaurantMenuIcon from "@mui/icons-material/RestaurantMenu";
import { CartContext } from "../context/CartContext";

const Navbar = () => {
  const { cart } = useContext(CartContext);
  const itemCount = cart.reduce((sum, i) => sum + (i.quantity || 1), 0);

  return (
    <AppBar position="static" color="inherit" elevation={1} sx={{ mb: 2 }}>
      <Container maxWidth="lg">
        <Toolbar disableGutters sx={{ display: "flex", gap: 2 }}>
          <RestaurantMenuIcon color="primary" sx={{ mr: 1 }} />
          <Typography
            variant="h6"
            component={RouterLink}
            to="/"
            sx={{ flexGrow: 1, textDecoration: "none", color: "inherit", fontWeight: 800 }}
          >
            Canteen
          </Typography>

          <Box sx={{ display: { xs: "none", sm: "flex" }, gap: 1 }}>
            <Button component={RouterLink} to="/" color="primary">Home</Button>
            <Button component={RouterLink} to="/orders" color="primary">Orders</Button>
            <Button component={RouterLink} to="/login" color="primary">Login</Button>
          </Box>

          <IconButton component={RouterLink} to="/cart" size="large" color="primary" aria-label="cart">
            <Badge badgeContent={itemCount} color="primary" overlap="circular">
              <ShoppingCartIcon />
            </Badge>
          </IconButton>
        </Toolbar>
      </Container>
    </AppBar>
  );
};

export default Navbar;
