import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Container,
  Typography,
  Paper,
  Box,
  Chip,
  Divider,
  List,
  ListItem,
  ListItemText,
  ListItemAvatar,
  Avatar,
} from "@mui/material";

const statusColor = (status) => {
  switch (status) {
    case "preparing":
      return "warning";
    case "on-the-way":
      return "secondary";
    case "delivered":
      return "success";
    case "cancelled":
      return "error";
    default:
      return "default";
  }
};

const Orders = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    if (!userId) return;
    axios
      .get(`http://localhost:5000/api/orders/${userId}`)
      .then((res) => setOrders(res.data || []))
      .catch((err) => console.error(err));
  }, []);

  if (!orders.length) {
    return (
      <Container maxWidth="md" sx={{ py: 8, textAlign: "center" }}>
        <Typography variant="h5" gutterBottom>
          You have no orders yet
        </Typography>
        <Typography color="text.secondary">
          Place your first order and it will appear here.
        </Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Typography variant="h4" fontWeight={800} gutterBottom>
        My Orders
      </Typography>
      {orders.map((order) => (
        <Paper key={order._id} elevation={1} sx={{ p: 2, mb: 2, borderRadius: 2 }}>
          <Box sx={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
            <Typography variant="subtitle1" fontWeight={700}>
              Order #{String(order._id).slice(-6)}
            </Typography>
            <Chip label={order.status} color={statusColor(order.status)} size="small" sx={{ textTransform: "capitalize" }} />
          </Box>

          <Divider sx={{ my: 2 }} />

          <List dense disablePadding>
            {order.items.map((i, idx) => (
              <ListItem key={`${i._id || i.name}-${idx}`} disableGutters secondaryAction={
                <Typography fontWeight={700}>₹{(Number(i.price) * Number(i.quantity)).toFixed(2)}</Typography>
              }>
                <ListItemAvatar>
                  <Avatar variant="rounded" src={i.image} alt={i.name}>
                    {i.name?.[0] || "F"}
                  </Avatar>
                </ListItemAvatar>
                <ListItemText
                  primary={`${i.name} × ${i.quantity}`}
                  secondary={i.category}
                />
              </ListItem>
            ))}
          </List>

          <Divider sx={{ my: 2 }} />

          <Box sx={{ display: "flex", justifyContent: "space-between" }}>
            <Typography color="text.secondary">Total</Typography>
            <Typography variant="h6" fontWeight={800}>₹{Number(order.totalAmount).toFixed(2)}</Typography>
          </Box>
        </Paper>
      ))}
    </Container>
  );
};

export default Orders;
